    <section class="featured-beers">
      <div class="inner-container">
        <h3>Featured Beers</h3>
        <div class="featured-beers-flex">
          <?php
          $beer_cpts = array('year-round-beers', 'seasonal-beers', 'barrel-aged-beers');

          foreach ($beer_cpts as $cpt) {
            $args = array(
              'post_type' => $cpt,
              'posts_per_page' => -1,
            );
            $beer_posts = get_posts($args);

            foreach ($beer_posts as $beer_post) {
              // Get the value of the featured label using ACF
              $is_featured = get_field('featured', $beer_post->ID);
              $beer_image = get_field('beer_image', $beer_post->ID);
              $beer_image_url = $beer_image['url'];
              $beer_image_alt = get_post_meta($beer_image['id'], '_wp_attachment_image_alt', true);
              // Check if the beer is featured
              if ($is_featured) {
                $beer_name = $beer_post->post_title;
                $beer_permalink = get_permalink($beer_post->ID);
                ?>
                <a href="<?php echo esc_url($beer_permalink); ?>">
                  <img src="<?php echo esc_url($beer_image_url); ?>" alt="<?php echo esc_attr($beer_image_alt); ?>"
                    class="featured-beer-image" />
                  <?php
                  // Output the beer name
                  echo "<h4 class=\"lead featured-beer-name\">$beer_name</h4>";
                  echo "</a>";
              }
            }
          }
          ?>
        </div>


      </div>
    </section>

  
      <?php
      $events = tribe_get_events(
        array(
          'posts_per_page' => 5,
          'start_date' => date('Y-m-d H:i:s'),
        )
      );

      if ($events):
        ?>
        <section class="events">
          <div class="inner-container">
            <h3>Events at Endeavour</h3>
          </div>
                    <div class="events-list">
                        <?php
                        foreach ( $events as $event ) :
                            setup_postdata( $event );
                            $featured_image_id = get_post_thumbnail_id( $event->ID );
                            $featured_image_url = wp_get_attachment_image_src( $featured_image_id, 'medium' );
            
                            ?>
                            <div class="event-item">
                                <?php if ( $featured_image_url ) : ?>
                                    <a href="<?php echo esc_url( get_permalink( $event ) ); ?>">
                                        <img src="<?php echo esc_url( $featured_image_url[0] ); ?>" alt="event - <?php echo esc_attr( get_the_title( $event ) ); ?>">
                                        <h3><?php echo esc_html( get_the_title( $event ) ); ?></h3>
                                    </a>
                                <?php endif; ?>
        
                               
                            </div>
        
                        <?php
                        endforeach;
                        wp_reset_postdata();
                        ?>
        
                    </div>

            </section>
        
        <?php
      endif;
      ?>
